import { INCREMENT, DECREMENT, SET_PRODUCTS } from "./counter.types";

const INITIAL_STATE = {
    count: 0,
    products: 0,
};

const reducer = (state = INITIAL_STATE, {action, payload} ) => {
    switch(action.type) {
        case INCREMENT: 
            return {
                ...state, count: state.count + 1,
            };
        case DECREMENT:
            return {
                ...state, count: state.count - 1,
            };
        case SET_PRODUCTS:
            return {
                ...state, products: state.products = payload
            }
        default: return state;
    }
};

export default reducer;
