import { SET_PRODUCTS } from "./products.types";

const INITIAL_STATE = {
    products: [],
};

export const products = (state = INITIAL_STATE, { type, payload }) => {
    switch(type) {
        case SET_PRODUCTS:
            return {
                ...state,
                products: payload
            }
        default: return state;
    }
};

// export default reducerProducts;
